package main

import "fmt"

func reverseStr(s string, k int) string {
	res := s
	for i := 0; i < len(s); i += 2 * k {
		start := i
		end := i + k - 1
		if end >= len(s) {
			end = len(s) - 1
		}

		reverse(res, start, end)

	}

	return res
}

func reverse(s string, start, end int) {
	i := start
	j := end

	for i < j {
		Swap(s, i, j)
		i++
		j--
	}

}

func Swap(s string, i, j int) string {
	r := []rune(s)
	tmp := r[i]
	r[i] = r[j]
	r[j] = tmp
	return string(r)
}

func main() {
	fmt.Println(reverseStr("", 312))
}
